// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {GatewayStorage} from "./GatewayStorage.sol";

/// @title ExocoreGatewayStorage
/// @notice Storage used by the ExocoreGateway contract.
/// @author ExocoreNetwork
contract ExocoreGatewayStorage is GatewayStorage {

    /// @dev The length of a deposit request, in bytes.
    // bytes32 token + bytes32 depositor + uint256 amount
    uint256 internal constant DEPOSIT_REQUEST_LENGTH = 96;

    /// @dev The length of a delegate request, in bytes.
    // bytes32 token + bytes32 delegator + bytes(42) operator + uint256 amount
    uint256 internal constant DELEGATE_REQUEST_LENGTH = 138;

    /// @dev The length of an undelegate request, in bytes.
    // bytes32 token + bytes32 delegator + bytes(42) operator + uint256 amount
    uint256 internal constant UNDELEGATE_REQUEST_LENGTH = 138;

    /// @dev The length of a withdraw principal request, in bytes.
    // bytes32 token + bytes32 withdrawer + uint256 amount
    uint256 internal constant WITHDRAW_PRINCIPAL_REQUEST_LENGTH = 96;

    /// @dev The length of a claim reward request, in bytes.
    // bytes32 token + bytes32 withdrawer + uint256 amount
    uint256 internal constant CLAIM_REWARD_REQUEST_LENGTH = 96;

    /// @dev The length of a deposit-then-delegate request, in bytes.
    // bytes32 token + bytes32 delegator + bytes(42) operator + uint256 amount
    uint256 internal constant DEPOSIT_THEN_DELEGATE_REQUEST_LENGTH = DELEGATE_REQUEST_LENGTH;

    // constants used for layerzero messaging
    /// @dev The gas limit for all the destination chains.
    uint128 internal constant DESTINATION_GAS_LIMIT = 500_000;

    /// @dev The msg.value for all the destination chains.
    uint128 internal constant DESTINATION_MSG_VALUE = 0;

    /// @notice A mapping from client chain IDs to whether the chain has been bootstrapped.
    /// @dev Used to ensure no repeated bootstrap requests are sent.
    mapping(uint32 clienChainId => bool) public chainToBootstrapped;

    /// @notice A mapping from client chain IDs to whether the chain has been registered.
    /// @dev Used to ensure that only registered client chains can interact with the gateway.
    mapping(uint32 clienChainId => bool registered) public isRegisteredClientChain;

    /// @notice A mapping from token address to whether the token is whitelisted.
    /// @dev Used to ensure no duplicate tokens are added to the whitelist.
    mapping(bytes32 token => bool whitelisted) public isWhitelistedToken;

    /// @notice Emitted when a precompile call fails.
    /// @param precompile Address of the precompile contract.
    /// @param nonce The LayerZero nonce
    event ExocorePrecompileError(address indexed precompile, uint64 nonce);

    /// @notice Emitted upon the registration of a new client chain.
    /// @param clientChainId The LayerZero chain ID of the client chain.
    event ClientChainRegistered(uint32 clientChainId);

    /// @notice Emitted upon the update of a client chain.
    /// @param clientChainId The LayerZero chain ID of the client chain.
    event ClientChainUpdated(uint32 clientChainId);

    /// @notice Emitted when a token is added to the whitelist.
    /// @param clientChainId The LayerZero chain ID of the client chain.
    /// @param token The address of the token.
    event WhitelistTokenAdded(uint32 clientChainId, bytes32 token);

    /// @notice Emitted when a token is updated in the whitelist.
    /// @param clientChainId The LayerZero chain ID of the client chain.
    /// @param token The address of the token.
    event WhitelistTokenUpdated(uint32 clientChainId, bytes32 token);

    /* --------- asset operations results and staking operations results -------- */
    /// @notice Emitted when reward is withdrawn.
    /// @param success Whether the withdrawal was successful.
    /// @param token The address of the token.
    /// @param withdrawer The address of the withdrawer.
    /// @param amount The amount of the token withdrawn.
    event WithdrawRewardResult(bool indexed success, bytes32 indexed token, bytes32 indexed withdrawer, uint256 amount);

    /// @notice Emitted when a deposit happens.
    /// @param success Whether the deposit was successful.
    /// @param token The address of the token.
    /// @param depositor The address of the depositor.
    /// @param amount The amount of the token deposited.
    event DepositResult(bool indexed success, bytes32 indexed token, bytes32 indexed depositor, uint256 amount);

    /// @notice Emitted when principal is withdrawn.
    /// @param success Whether the withdrawal was successful.
    /// @param token The address of the token.
    /// @param withdrawer The address of the withdrawer.
    /// @param amount The amount of the token withdrawn.
    event WithdrawPrincipalResult(
        bool indexed success, bytes32 indexed token, bytes32 indexed withdrawer, uint256 amount
    );

    /// @notice Emitted upon delegation.
    /// @param success Whether the delegation was successful.
    /// @param token The address of the token.
    /// @param delegator The address of the delegator.
    /// @param operator The Exo account address of the operator.
    /// @param amount The amount of the token delegated.
    event DelegateResult(
        bool indexed success, bytes32 indexed token, bytes32 indexed delegator, string operator, uint256 amount
    );

    /// @notice Emitted upon undelegation
    /// @param success Whether the undelegation was successful.
    /// @param token The address of the token.
    /// @param undelegator The address of the undelegator.
    /// @param operator The Exo account address of the operator.
    /// @param amount The amount of the token undelegated.
    event UndelegateResult(
        bool indexed success, bytes32 indexed token, bytes32 indexed undelegator, string operator, uint256 amount
    );

    /// @notice Thrown when the execution of a request fails
    /// @param act The action that failed.
    /// @param nonce The LayerZero nonce.
    /// @param reason The reason for the failure.
    error RequestExecuteFailed(Action act, uint64 nonce, bytes reason);

    /// @notice Thrown when the execution of a precompile call fails.
    /// @param selector_ The function selector of the precompile call.
    /// @param reason The reason for the failure.
    error PrecompileCallFailed(bytes4 selector_, bytes reason);

    /// @notice Thrown when the request length is invalid.
    /// @param act The action that failed.
    /// @param expectedLength The expected length of the request.
    /// @param actualLength The actual length of the request.
    error InvalidRequestLength(Action act, uint256 expectedLength, uint256 actualLength);

    /// @notice Thrown when a deposit request fails.
    /// @param srcChainId The source chain ID.
    /// @param lzNonce The LayerZero nonce.
    /// @dev This is considered a critical error.
    error DepositRequestShouldNotFail(uint32 srcChainId, uint64 lzNonce);

    /// @notice Thrown when a client chain registration fails
    /// @param clientChainId The LayerZero chain ID of the client chain.
    error RegisterClientChainToExocoreFailed(uint32 clientChainId);

    /// @notice Thrown when a whitelist token addition fails
    /// @param token The address of the token.
    error AddWhitelistTokenFailed(bytes32 token);

    /// @notice Thrown when a whitelist token update fails
    /// @param token The address of the token.
    error UpdateWhitelistTokenFailed(bytes32 token);

    /// @notice Thrown when the whitelist tokens input is invalid.
    error InvalidWhitelistTokensInput();

    /// @notice Thrown when the client chain ID is not registered.
    /// @param clientChainId The LayerZero chain ID of the client chain.
    error ClientChainIDNotRegisteredBefore(uint32 clientChainId);

    /// @notice Thrown when the whitelist tokens list is too long.
    error WhitelistTokensListTooLong();

    /// @dev Storage gap to allow for future upgrades.
    uint256[40] private __gap;

}

src/libraries/Errors.sol.content:
pragma solidity ^0.8.19;

/// @dev @title Errors library
/// @dev @notice A library for all errors that can be thrown in the Exocore contracts
/// @dev All errors in Exocore follow the following syntax: 'error ContractNameErrorName(arg1, arg2, ...)', where
/// @dev 'ContractName' is the name of the contract
/// @dev that the error originates from and 'ErrorName' is the name of the error. The arguments are optional and are
/// used to
/// @dev provide additional context to the error
/// @dev 'Global' errors are those that are thrown from various contracts throughout the protocol and do not have a
/// @dev 'ContractName' prefix

library Errors {

    /////////////////////
    //  Global Errors  //
    /////////////////////

    /// @dev Thrown when the passed-in address is the zero address, i.e. address(0)
    error ZeroAddress();

    /// @dev Thrown when passed-in amount is zero
    error ZeroAmount();

    /// @dev Thrown wehn the passed-in value is zero
    /// @dev This is used when the value in question is not an amount
    error ZeroValue();

    /// @dev Index out of array bounds
    error IndexOutOfBounds();

    ////////////////////////
    //  Bootstrap Errors  //
    ////////////////////////

    /// @dev Bootstrap: spawn time should be in the future
    error BootstrapSpawnTimeAlreadyPast();

    /// @dev Bootstrap: spawn time should be greater than offset duration
    error BootstrapSpawnTimeLessThanDuration();

    /// @dev Bootstrap: lock time should be in the future
    error BootstrapLockTimeAlreadyPast();

    /// @dev Bootstrap: operation not allowed after lock time
    error BootstrapBeforeLocked();

    /// @dev Bootstrap: token should be not whitelisted before
    /// @param token The address of the token already whitelisted
    error BootstrapAlreadyWhitelisted(address token);

    /// @dev Bootstrap: Ethereum address already linked to a validator
    /// @param validator The Ethereum address of the validator
    error BootstrapValidatorAlreadyHasAddress(address validator);

    /// @dev Bootstrap: Validator with this Exocore address is already registered
    error BootstrapValidatorAlreadyRegistered();

    /// @dev Bootstrap: Consensus public key already in use
    /// @param publicKey The public key that is already in use
    error BootstrapConsensusPubkeyAlreadyUsed(bytes32 publicKey);

    /// @dev Bootstrap: Validator name already in use
    error BootstrapValidatorNameAlreadyUsed();

    /// @dev Bootstrap: Invalid commission
    error BootstrapInvalidCommission();

    /// @dev Bootstrap: validator does not exist
    error BootstrapValidatorNotExist();

    /// @dev Bootstrap: Commission already edited once
    error BootstrapComissionAlreadyEdited();

    /// @dev Bootstrap: Rate exceeds max rate
    error BootstrapRateExceedsMaxRate();

    /// @dev Bootstrap: Rate change exceeds max change rate
    error BootstrapRateChangeExceedsMaxChangeRate();

    /// @dev Bootstrap: insufficient deposited balance
    error BootstrapInsufficientDepositedBalance();

    /// @dev Bootstrap: insufficient withdrawable balance
    error BootstrapInsufficientWithdrawableBalance();

    /// @dev Bootstrap: insufficient delegated balance
    error BootstrapInsufficientDelegatedBalance();

    /// @dev Bootstrap: no ether required for delegation/undelegation
    error BootstrapNoEtherForDelegation();

    /// @dev Bootstrap: not yet in the bootstrap time
    error BootstrapNotSpawnTime();

    /// @dev Bootstrap: not yet bootstrapped
    error BootstrapAlreadyBootstrapped();

    /// @dev Bootstrap: client chain initialization data is malformed
    error BootstrapClientChainDataMalformed();

    //////////////////////////////////
    //  BootstrapLzReceiver Errors  //
    //////////////////////////////////

    /// @dev BootstrapLzReceiver: could only be called from this contract itself with low level call
    error BootstrapLzReceiverOnlyCalledFromThis();

    /// @dev BootstrapLzReceiver: invalid action
    error BootstrapLzReceiverInvalidAction();

    /////////////////////////////////
    //  ClientChainGateway Errors  //
    /////////////////////////////////

    /// @dev ClientChainGateway: tokens length should not execeed 255
    error ClientChainGatewayAddWhitelistTooManyTokens();

    /// @dev ClientChainGateway: token should not be whitelisted before
    error ClientChainGatewayAlreadyWhitelisted();

    //////////////////////////////////////
    //  ClientGatewayLzReceiver Errors  //
    //////////////////////////////////////

    /// @dev ClientChainLzReceiver: could only be called from this contract itself with low level call
    error ClientGatewayLzReceiverOnlyCalledFromThis();

    ///////////////////////////////
    //  CustomProxyAdmin Errors  //
    ///////////////////////////////

    /// @dev CustomProxyAdmin: sender must be bootstrapper
    error CustomProxyAdminOnlyCalledFromBootstrapper();

    /// @dev CustomProxyAdmin: sender must be the proxy itself
    error CustomProxyAdminOnlyCalledFromProxy();

    /////////////////////////
    //  ExoCapsule Errors  //
    /////////////////////////

    /// @dev ExoCapsule: withdrawal amount is larger than staker's withdrawable balance
    error ExoCapsuleWithdrawalAmountExceeds();

    /// @dev ExoCapsule: withdrawNonBeaconChainETHBalance: amountToWithdraw is greater than nonBeaconChainETHBalance
    error ExoCapsuleNonBeaconChainWithdrawalAmountExceeds();

    /// @dev ExoCapsule: timestamp should be greater than beacon chain genesis timestamp
    error ExoCapsuleTimestampBeforeGenesis();

    /////////////////////////////
    //  ExocoreGateway Errors  //
    /////////////////////////////

    /// @dev ExocoreGateway: can only be called from this contract itself with a low-level call
    error ExocoreGatewayOnlyCalledFromThis();

    /// @dev ExocoreGateway: failed to get client chain ids
    error ExocoreGatewayFailedToGetClientChainIds();

    /// @dev ExocoreGateway: failed to decode client chain ids
    error ExocoreGatewayFailedToDecodeClientChainIds();

    /// @dev ExocoreGateway: client chain should be registered before setting peer to change peer address
    error ExocoreGatewayNotRegisteredClientChainId();

    ////////////////////////////////////////
    //  NativeRestakingController Errors  //
    ////////////////////////////////////////

    /// @dev NativeRestakingController: native restaking is not enabled
    error NativeRestakingControllerNotWhitelisted();

    /// @dev NativeRestakingController: stake value must be exactly 32 ether
    error NativeRestakingControllerInvalidStakeValue();

    /// @dev NativeRestakingController: message sender has already created the capsule
    error NativeRestakingControllerCapsuleAlreadyCreated();

    ////////////////////
    //  Vault Errors  //
    ////////////////////

    /// @dev Vault: caller is not the gateway
    error VaultCallerIsNotGateway();

    /// @dev Vault: withdrawal amount is larger than depositor's withdrawable balance
    error VaultWithdrawalAmountExceeds();

    /// @dev Vault: total principal unlock amount is larger than the total deposited amount
    error VaultPrincipalExceedsTotalDeposit();

    /// @dev Vault: total principal unlock amount is larger than the total deposited amount
    error VaultTotalUnlockPrincipalExceedsDeposit();

}

src/lzApp/OAppCoreUpgradeable.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.20;

import {ILayerZeroEndpointV2, IOAppCore} from "@layerzero-v2/oapp/contracts/oapp/interfaces/IOAppCore.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

/**
 * @title OAppCoreUpgradeable
 * @dev Abstract contract implementing the IOAppCore interface with basic OApp configurations.
 */
abstract contract OAppCoreUpgradeable is IOAppCore, OwnableUpgradeable {

    // The LayerZero endpoint associated with the given OApp
    ILayerZeroEndpointV2 public immutable endpoint;

    // Mapping to store peers associated with corresponding endpoints
    mapping(uint32 eid => bytes32 peer) public peers;

    /**
     * @dev Constructor to initialize the OAppCore with the provided endpoint and delegate.
     * @param _endpoint The address of the LOCAL Layer Zero endpoint.
     */
    constructor(address _endpoint) {
        require(_endpoint != address(0), "layerzero endpoint should not be empty");
        endpoint = ILayerZeroEndpointV2(_endpoint);

        _disableInitializers();
    }

    /**
     * @dev The delegate typically should be set as the owner of the contract.
     * @param _delegate The delegate capable of making OApp configurations inside of the endpoint.
     */
    function __OAppCore_init(address _delegate) internal onlyInitializing {
        if (_delegate == address(0)) {
            revert InvalidDelegate();
        }
        endpoint.setDelegate(_delegate);
        _transferOwnership(_delegate);
    }

    /**
     * @dev The delegate typically should be set as the owner of the contract.
     * @param _delegate The delegate capable of making OApp configurations inside of the endpoint.
     */
    function __OAppCore_init_unchained(address _delegate) internal onlyInitializing {
        if (_delegate == address(0)) {
            revert InvalidDelegate();
        }
        endpoint.setDelegate(_delegate);
    }

    /**
     * @notice Sets the peer address (OApp instance) for a corresponding endpoint.
     * @param _eid The endpoint ID.
     * @param _peer The address of the peer to be associated with the corresponding endpoint.
     *
     * @dev Only the owner/admin of the OApp can call this function.
     * @dev Indicates that the peer is trusted to send LayerZero messages to this OApp.
     * @dev Set this to bytes32(0) to remove the peer address.
     * @dev Peer is a bytes32 to accommodate non-evm chains.
     */
    function setPeer(uint32 _eid, bytes32 _peer) public virtual onlyOwner {
        peers[_eid] = _peer;
        emit PeerSet(_eid, _peer);
    }

    /**
     * @notice Internal function to get the peer address associated with a specific endpoint; reverts if NOT set.
     * ie. the peer is set to bytes32(0).
     * @param _eid The endpoint ID.
     * @return peer The address of the peer associated with the specified endpoint.
     */
    function _getPeerOrRevert(uint32 _eid) internal view virtual returns (bytes32) {
        bytes32 peer = peers[_eid];
        if (peer == bytes32(0)) {
            revert NoPeer(_eid);
        }
        return peer;
    }

    /**
     * @notice Sets the delegate address for the OApp.
     * @param _delegate The address of the delegate to be set.
     *
     * @dev Only the owner/admin of the OApp can call this function.
     * @dev Provides the ability for a delegate to set configs, on behalf of the OApp, directly on the Endpoint
     * contract.
     */
    function setDelegate(address _delegate) public onlyOwner {
        endpoint.setDelegate(_delegate);
    }

}

