const { ethers, JsonRpcProvider } = require("ethers");

const provider = new JsonRpcProvider("http://127.0.0.1:8545");

var signer = new ethers.Wallet("0xC2B2D88470A1516583BB91A167B7CE70EF962F6FC865E37C6CC3CDC1FCC3C2C0", provider);
var rawdata = "";

provider.getBlockNumber().then(console.log);
console.log(signer.address);

(async () => {
    var tx = await signer.sendTransaction({
        to: "0x0000000000000000000000000000000000000804",
        value: 0,
        data: rawdata
    });
    console.log(tx);
    await new Promise(resolve => setTimeout(resolve, 3000));
    var receipt = await provider.getTransactionReceipt(tx.hash);
    console.log(receipt);
})();